cdef4c, 0x34567f7b
		# Memory space for decrypted output
		decrypted:   .space 8000  													 
		  												
		  									
		

.text
.globl main

main:
		# Initializations
		
		# Parameters
		addi $13, $0, 1000   				# Number of blocks to decrypt (512 block = 1000 word)
		addi $12, $0, 150  					# Number of rounds

		# Pointers to ciphertext and decrypted text
		addi $8, $0, 0  					# Pointer to ciphertext block
		addi $9, $0, 0  					# Pointer to decrypted text block

		# Load Initialization Vector (IV) into registers
		lw $10, iv($0)  					# Load first word of IV , IV[0]

#-----> if word addresable replace with lw $11, iv + 1 ($0)
		lw $11, iv+4($0)  					# Load second word of IV, IV[1]

		# Load encryption key
		lw $15, key($0)

decryptLoop:
		
		beq $13, $zero, doneDecryption  	# Exit when all blocks are decrypted

		# Load the next cyphertext block     (2 words)
		lw $14, ciphertext+0($8)  			# Load left half of ciphertext block  (L)

#-----> if word addresable replace with  lw $16, ciphertext+1($8)
		lw $16, ciphertext+4($8)  			# Load right half of ciphertext block (R)

		# Save current ciphertext block as IV for the next iteration
		add $24, $0, $14  					# Store left half  (Ln) for next IV
		add $25, $0, $16  					# Store right half (Rn) for next IV

		# Initialize round counter
		add $26, $0, $0  					# Set round counter to 0

feistelDecryptLoop:
		beq $26, $12, storeDecrypted  		# Stop when all rounds are complete

		# Apply Feistel function in reverse
											# f(Ln, K) = (Ln XOR K) >> 3
		xor $23, $14, $15  					# XOR left half with key  
		srl $23, $23, 3  					# Shift right by 3 bits (simplified Feistel function)

		# Reverse Feistel swap
		xor $20, $16, $23 					# Compute new left half      , New L = R XOR f(L, K)
		
		# Swap
		add $16, $0, $14  					# Move old left half to right, R = old L
		add $14, $0, $20  					# Move new left half to left , L = new L

		# Increment round counter and repeat
		addi $26, $26, 1
		jal feistelDecryptLoop  			# Jump to next Feistel round

storeDecrypted:
		# Apply CBC decryption: XOR with previous IV
		xor $14, $14, $10  					# Decrypted left half  = L XOR IV[0]
		xor $16, $16, $11  					# Decrypted right half = R XOR IV[1]

		# Store the decrypted block in memory
		sw $14, decrypted+0($9) 			# Store decrypted word (L_final)

#-----> if word addresable replace with  sw $16, decrypted+1($9)
		sw $16, decrypted+4($9) 			# Store decrypted word (R_final)

		# Update IV for next block
		add $10, $0, $24  					# IV[0] = previous L (ciphertext)
		add $11, $0, $25  					# IV[1] = previous R (ciphertext)

		# Move pointers to next block
		
#-----> if word addresable replace with  addi $8, $8, 2 and addi $9, $9, 2
		addi $8, $8, 8  					# increment ciphertext pointer
		addi $9, $9, 8  					# increment decrypted text pointer

		# Decrease block count
		addi $13, $13, -1
		jal decryptLoop  					# Continue decryption for next block

doneDecryption:
		li $v0, 10  						# Exit program
		syscall
	